function getCountDown(c){var a=new Date,a=a.getTime(),b=c.getTime()-a;c=Math.floor(b/864E5);var b=b-864E5*c,a=Math.floor(b/36E5),b=b-36E5*a,d=Math.floor(b/6E4),b=Math.floor((b-6E4*d)/1E3);return(10>c?"0"+c:c)+" : "+(10>a?"0"+a:a)+" : "+(10>d?"0"+d:d)+" : "+(10>b?"0"+b:b)}
function initNumbers(){var c=[];for(i=11;0<=i;i--){var a=$('<span class="clock-number"></span>');a.text((0==i?12:i)+"");a.css("left",260+215*Math.cos(1.57-Math.PI/180*i*30)+"px");a.css("top",230-215*Math.sin(1.57-Math.PI/180*i*30)+"px");c.push(a)}return c}$(document).ready(function(){SVG("canvas").clock("100%").start();var c=initNumbers();$("#time-container").append(c);$("#canvas").everyTime("1s",function(a){a=new Date;a.setYear(2014);a.setMonth(7);a.setDate(5);a.setHours(18,40,0,0);$("#timeleft").text(getCountDown(a))})});